import React, {Fragment, useState} from "react";

const InputAppartment = () => {

    const [appartment, setAppartment] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const body = {appartment};
            const response = await fetch("http://localhost:8000/", {
                method: "POST",
                headers: {"Content-Type": "application.json"},
                body: JSON.stringify(body)
            });

            console.log("ok");
        } catch (err) {
            console.error(err.message);
        }
    };

    const handleChange = (event, number) => {
        event.preventDefault()
        if (number === 1) {
            console.log("premier");
        }

    }


    return (
        <Fragment>
            <form onClick={handleSubmit}>
                <input type="text" className="form-control" placeholder="owner" onChange={handleChange("un")} />
                <input type="text" className="form-control" placeholder="rooms" onChange={handleChange} />
                <input type="text" className="form-control" placeholder="location" onChange={handleChange} />
                <button className="btn btn-success">Add</button>
            </form>
        </Fragment>
    )

}

export default InputAppartment;
