import React, {Fragment, useState} from "react";

class SearchAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            appartments: [],
            rooms: null,
            location: null,
            result: false
        };
    }

    handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;


        this.setState({
                [name]: value
            }
        )

        console.log(this.state)


    }

    handleSubmit = async (event) => {
        event.preventDefault();
        const {appartments, rooms, location} = this.state
        console.log(rooms, location)
        try {

            fetch("http://localhost:8000/appartment", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"

                },


            }).then(function(response) {
                return response.json()

            }).then(function(responseData) {
                console.log((responseData))
                for (var i in responseData) {
                    console.log(responseData[i].rooms)

                    if (responseData[i].rooms === parseInt(rooms) && responseData[i].location === location) {
                        const rooms = responseData[i].rooms;
                        const location= responseData[i].location;
                        this.setState({rooms: rooms, location: location, result: true})
                        const appartments = appartments.slice()
                        appartments.push({rooms: rooms, location: location})
                        this.setState({appartments: appartments})
                        console.log(appartments)
                    }

                }



            })



        } catch (err) {
            console.error(err.message)
        }

    }

    handleRender = () => {
        const {rooms, location} = this.state
        return(
            <div>

            </div>
        )
    }

    render() {

        const name = ["rooms", "location"];
        const {result} = this.state;
        return (
            <Fragment>
                <form className="text-center mt-5">
                    <label>
                        {name.map((index) => {
                            return (
                                <input type="text" className="form-control" name={index} placeholder={index} onChange={this.handleChange} />
                            )
                        })}

                    </label>


                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>
                {result === true ? this.handleRender : ''}

            </Fragment>
        )
    }


}

export default SearchAppartment;
