import React, {Fragment, useState} from "react";

const InputAppartment = () => {

    const [appartmentslist, setAppartmentsList] = useState([])
    const [appartment, setAppartment] = useState({
        owner: null,
        rooms: null,
        location: null
    });


    const handleChange = async (event) => {
        const target = event.target
        const name = target.name

        setAppartment({
            [name]: target.value
            }
        )
        console.log(appartment)
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        const owner = appartment.owner;
        const rooms = appartment.rooms;
        const location = appartment.location;

        const appart = {owner: owner, rooms: rooms, location: location};

        const appartmentsliste = appartmentslist.slice();
        appartmentsliste.push(appartment);

        setAppartmentsList(appartmentslist)





    };




    return (
        <Fragment>
            <form onClick={handleSubmit}>
                <input type="text" className="form-control" name="owner" placeholder="owner" onChange={handleChange} />
                <input type="text" className="form-control" name="rooms" placeholder="rooms" onChange={handleChange} />
                <input type="text" className="form-control" name="location" placeholder="location" onChange={handleChange} />
                <button className="btn btn-success">Add</button>
            </form>
        </Fragment>
    )

}

export default InputAppartment;
