import React, {Fragment, useState} from "react";

class InputAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            owner: '',
            rooms: null,
            location: ''
        };
    }

    handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({
            [name]: value
            }
        )
    }

    handleSubmit = (event) => {
        event.preventDefault()
        try {
            const body = this.state
