        }

    }

    render() {
        return (
            <Fragment>
                <form>
                    <input type="text" className="form-control" name="owner" placeholder="owner" onChange={this.handleChange} />
                    <input type="text" className="form-control" name="rooms" placeholder="rooms" onChange={this.handleChange} />
                    <input type="text" className="form-control" name="location" placeholder="location" onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>
            </Fragment>
        )
    }


}

export default InputAppartment;
