import React, {Fragment, useState} from "react";

const InputAppartment = () => {

    const [appartment, setAppartment] = useState({
        owner: '',
        rooms: null,
        location: null,
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log("result", appartment.owner, appartment.rooms, appartment.location)
    };

    const handleChange = async (event) => {
        console.log(appartment.owner)
        if (event.target.name === "owner") {
            const value1 = event.currentTarget.value;
            setAppartment({owner: value1})
        }

        if (event.target.name === "rooms") {
            const value2 = event.currentTarget.value;
            setAppartment({rooms: value2})
        }

        if (event.target.name === "location") {
            const value3 = event.currentTarget.value;
            setAppartment({location: value3})
        }

    }




    return (
        <Fragment>
            <form onClick={handleSubmit}>
                <input type="text" className="form-control" name="owner" placeholder="owner" onChange={handleChange} />
                <input type="text" className="form-control" name="rooms" placeholder="rooms" onChange={handleChange} />
                <input type="text" className="form-control" name="location" placeholder="location" onChange={handleChange} />
                <button className="btn btn-success">Add</button>
            </form>
        </Fragment>
    )

}

export default InputAppartment;
