import React, {Fragment, useState} from "react";

class SearchAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            keyword: ''
        };
    }

    handleChange = (event) => {
        let keyword = event.target.name;
        let value = event.target.value;
        this.setState({
                keyword: value
            }
        )

        console.log(this.state)


    }

    handleSubmit = async (event) => {
        event.preventDefault();
        const keyword = this.state.keyword;
        try {
            console.log(keyword)
            fetch("http://localhost:8000/appartment", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"

                },


            }).then(function(response) {
                return response.json()

            }).then(function(responseData) {
                responseData.map((index) => {
                    for (var key in index) {

                            if(index[key] == keyword) {
                                console.log("ok")
                            }

                    }
                })
            })



        } catch (err) {
            console.error(err.message)
        }

    }

    render() {

        const name = ["rooms", "location"];
        return (
            <Fragment>
                <form>
                    <label>
                        {name.map((index) => {
                            return (
                                <input type="text" className="form-control" name={index} placeholder={index} onChange={this.handleChange} />
                            )
                        })}

                    </label>


                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>
            </Fragment>
        )
    }


}

export default SearchAppartment;
