import React, {Fragment, useState} from "react";

class SearchAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            appartment: [],
            rooms: null,
            location: null,
            result: false
        };
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;


        this.setState({
                [name]: value
            }
        )




    }

     handleSubmit = async (event) => {
        event.preventDefault();

        const {rooms, location} = this.state;



        try {

                const response = await fetch(`http://localhost:8000/appartment/search/${rooms}/${location}`);
                const jsonData = await response.json();
                this.setState(jsonData)


        } catch (err) {
            console.error(err.message)
        }

    }



    handleRender = () => {
        const {rooms, location} = this.state
        return(
            <div>

            </div>
        )
    }

    render() {

        const name = ["rooms", "location"];
        const {result} = this.state;
        return (
            <Fragment>
                <form className="text-center mt-5">
                    <label>
                        {name.map((index) => {
                            return (
                                <input type="text" className="form-control" name={index} placeholder={index} onChange={this.handleChange} />
                            )
                        })}

                    </label>


                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>
                {result === true ? this.handleRender : ''}

            </Fragment>
        )
    }


}

export default SearchAppartment;
