import React, {Fragment, useState} from "react";

class SearchAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            appartment: [],
            rooms: null,
            location: null,
            result: false
        };
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;


        this.setState({
                [name]: value
            }
        )




    }

     handleSubmit (event)  {
        event.preventDefault();
        const {rooms, location} = this.state

        try {

            fetch("http://localhost:8000/appartment", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"

                },


            }).then(function(response) {
                return response.json()

            }).then(function(responseData) {

                for (var i in responseData) {


                    if (responseData[i].rooms === parseInt(rooms) && responseData[i].location === location) {
                        const rooms = responseData[i].rooms;
                        const location= responseData[i].location;
                        console.log("ok")
                        this.setState({rooms: rooms, location: location})

                    }



                }




            })

            console.log(rooms, location)


        } catch (err) {
            console.error(err.message)
        }

    }

    handleRender = () => {
        const {rooms, location} = this.state
        return(
            <div>

            </div>
        )
    }

    render() {

        const name = ["rooms", "location"];
        const {result} = this.state;
        return (
            <Fragment>
                <form className="text-center mt-5">
                    <label>
                        {name.map((index) => {
                            return (
                                <input type="text" className="form-control" name={index} placeholder={index} onChange={this.handleChange} />
                            )
                        })}

                    </label>


                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>
                {result === true ? this.handleRender : ''}

            </Fragment>
        )
    }


}

export default SearchAppartment;
