import React, {Fragment, useState} from "react";

class SearchAppartment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            appartments: null,
            rooms: null,
            location: null,
        };
        this.handleSubmit = this.handleSubmit.bind(this)
    }



    handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;


        this.setState({
                [name]: value
            }
        )




    }

     handleSubmit = async (event) => {
        event.preventDefault();

        const {rooms, location, appartments} = this.state;


        if (rooms !== null) {
            try {

                const response = await fetch(`http://localhost:8000/appartment/search/${rooms}/${location}`);
                const jsonData = await response.json();


                this.setState({appartments: jsonData})




            } catch (err) {
                console.error(err.message)
            }

        } else {

            try {

                const response = await fetch(`http://localhost:8000/appartment/search/${location}`);
                const jsonData = await response.json();


                this.setState({appartments: jsonData})




            } catch (err) {
                console.error(err.message)
            }


        }

    }

    handleRender = () => {
        const {rooms, location} = this.state
        return(
            <div>

            </div>
        )
    }

    render() {

        const location = [1,2,3,4,5,6,7,8,9];
        let {appartments} = this.state

        return (
            <Fragment>
                <form className="text-center mt-5">
                    <label>
                        {location.map((index) => {
                            return (
                                <div>
                                <input type="text" className="form-control" name={index} placeholder={index} onChange={this.handleChange} />
                            <label htmlFor="pet-select">Choose a pet:</label>

                            <select name="pets" id="pet-select">
                                <option value="">--Please choose an option--</option>
                                <option value="dog">Dog</option>
                                <option value="cat">Cat</option>
                                <option value="hamster">Hamster</option>
                                <option value="parrot">Parrot</option>
                                <option value="spider">Spider</option>
                                <option value="goldfish">Goldfish</option>
                            </select>
                                </div>
                            )
                        })}

                    </label>

                    <button className="btn btn-success" onClick={this.handleSubmit}>Add</button>
                </form>

                {appartments === null ? appartments = '' : appartments.map((index) => {
                    return (
                        <div className="text-center mt-5">
                            <p>{index.owner}</p>
                            <p>{index.rooms}</p>
                            <p>{index.location}</p>
                        </div>

                    )

                })
                }


            </Fragment>
        )
    }


}

export default SearchAppartment;
